class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // initial state for pwd length and all char bools
      uppercase: "ABCDEFGHIJKLMNOPQRSTUVWXYZ",
      lowercase: "abcdefghijklmnopqrstuvwxyz",
      numbers: "0123456789",
      characters: "!@#$%^&*()_+=?",
      password: "",
    };
  }
  getCriteria = () => {
    const passlength = window.prompt(
      "How long would you like your password to be"
    );
    this.setState({ passlength: passlength });
  };

  render() {
    // run all the above methods to calculate the password before rendering

    return <div className="password button" getCriteria={this.getCriteria} />;
  }
}
export default App;
